const message = $input.item.json.message;
const text = message.text || '';

if (!text || text.trim().length === 0) {
  return {
    error: true,
    message: 'Сообщение пустое. Отправьте ссылку на контент.',
    chatId: message.chat.id
  };
}

function extractUrl(text) {
  const urlRegex = /(https?:\/\/[^\s]+)/g;
  const matches = text.match(urlRegex);
  return matches ? matches[0] : null;
}

function isValidUrl(url) {
  try {
    const urlObj = new URL(url);
    return urlObj.protocol === 'http:' || urlObj.protocol === 'https:';
  } catch {
    return false;
  }
}

function detectPlatform(url) {
  const lowerUrl = url.toLowerCase();
  if (lowerUrl.includes('youtube.com') || lowerUrl.includes('youtu.be')) {
    return 'youtube';
  } else if (lowerUrl.includes('instagram.com')) {
    return 'instagram';
  } else if (lowerUrl.includes('twitter.com') || lowerUrl.includes('x.com')) {
    return 'twitter';
  } else if (lowerUrl.includes('music.yandex')) {
    return 'yandex_music';
  } else if (lowerUrl.includes('tiktok.com') || lowerUrl.includes('vm.tiktok.com')) {
    return 'tiktok';
  }
  return 'unknown';
}

const url = extractUrl(text);

if (!url) {
  return {
    error: true,
    message: 'Не найдена ссылка в сообщении. Отправьте ссылку на контент.',
    chatId: message.chat.id
  };
}

if (!isValidUrl(url)) {
  return {
    error: true,
    message: 'Неверный формат URL',
    chatId: message.chat.id
  };
}

const platform = detectPlatform(url);

return {
  url: url,
  platform: platform,
  chatId: message.chat.id,
  messageId: message.message_id,
  error: false
};

const data = $input.item.json || $json;
const platform = $json.platform || data.platform || null;
const baseChatId = $json.chatId || $json.chat_id || data.chat_id || data.chatId;

if (data.error === true || data.error === 'true' || data.success === false || data.success === 'false' || (data.statusCode && data.statusCode >= 400)) {
  return {
    json: {
      error: true,
      message: data.message || data.error || 'Ошибка при получении контента',
      chatId: baseChatId
    }
  };
}

const isSuccess = data.success === true || data.success === 'true' || data.status === 'success' || data.statusCode === 200 || (data.statusCode >= 200 && data.statusCode < 300) || data.file_path || data.file_url || data.url || data.download_url || (data.data && (data.data.file_path || data.data.url));

if (!isSuccess) {
  return {
    json: {
      error: true,
      message: 'Не удалось получить медиа-файл',
      chatId: baseChatId
    }
  };
}

const filePath = data.file_path || data.file_url || data.url || data.download_url || (data.data && (data.data.file_path || data.data.url)) || null;

let metadata = data.metadata || {};
if (!metadata && data.data) {
  if (data.data.metadata) {
    metadata = data.data.metadata;
  } else if (!data.data.file_path) {
    metadata = data.data;
  }
}
if (!metadata) {
  metadata = {};
}

let contentType = data.type || data.file_type || data.media_type || data.content_type || null;

if (!contentType && filePath) {
  const extension = filePath.split('.').pop().toLowerCase().split('?')[0];
  if (extension === 'mp4' || extension === 'webm' || extension === 'mov' || extension === 'avi' || extension === 'mkv' || extension === 'flv' || extension === 'm4v') {
    contentType = 'video';
  } else if (extension === 'mp3' || extension === 'm4a' || extension === 'ogg' || extension === 'wav' || extension === 'flac' || extension === 'aac') {
    contentType = 'audio';
  }
}

const chatId = data.chat_id || data.chatId || baseChatId || null;
const textContent = data.text || data.message || data.content || metadata.text || null;

if (filePath) {
  const lowerPath = filePath.toLowerCase();
  const isVideo = contentType === 'video' || lowerPath.indexOf('.mp4') >= 0 || lowerPath.indexOf('.webm') >= 0 || lowerPath.indexOf('.mov') >= 0 || lowerPath.indexOf('video') >= 0 || platform === 'youtube' || platform === 'instagram' || platform === 'tiktok';
  const isAudio = contentType === 'audio' || lowerPath.indexOf('.mp3') >= 0 || lowerPath.indexOf('.m4a') >= 0 || lowerPath.indexOf('.ogg') >= 0 || lowerPath.indexOf('audio') >= 0 || platform === 'yandex_music';

  if (isVideo) {
    return {
      json: {
        file_path: filePath,
        chat_id: chatId,
        platform: platform,
        metadata: metadata,
        content_type: 'video',
        has_video: true,
        has_audio: false,
        has_text: false
      }
    };
  }

  if (isAudio) {
    return {
      json: {
        file_path: filePath,
        chat_id: chatId,
        platform: platform,
        metadata: metadata,
        content_type: 'audio',
        has_video: false,
        has_audio: true,
        has_text: false
      }
    };
  }

  if (platform === 'youtube' || platform === 'instagram' || platform === 'tiktok') {
    return {
      json: {
        file_path: filePath,
        chat_id: chatId,
        platform: platform,
        metadata: metadata,
        content_type: 'video',
        has_video: true,
        has_audio: false,
        has_text: false
      }
    };
  }
}

if (textContent && !filePath) {
  if (!chatId) {
    return {
      json: {
        error: true,
        message: 'Не удалось определить chat_id',
        chatId: baseChatId
      }
    };
  }

  let textToSend = String(textContent).trim();
  if (textToSend.length > 4096) {
    textToSend = textToSend.substring(0, 4093) + '...';
  }

  return {
    json: {
      text_content: textToSend,
      text: textToSend,
      chat_id: String(chatId),
      chatId: String(chatId),
      platform: platform,
      metadata: metadata,
      content_type: 'text',
      has_video: false,
      has_audio: false,
      has_text: true
    }
  };
}

return {
  json: {
    file_path: filePath,
    chat_id: chatId,
    platform: platform,
    metadata: metadata,
    content_type: contentType || 'unknown',
    has_video: false,
    has_audio: false,
    has_text: false,
    error: true,
    message: 'Не удалось определить тип контента'
  }
};
